import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";

function App() {
  const [quizData, setQuizData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(""); 
  const [numQuestions, setNumQuestions] = useState(""); 
  const [difficulty, setDifficulty] = useState(""); 
  const [questionType, setQuestionType] = useState("multiple"); 
  const [timeLeft, setTimeLeft] = useState(30);
  const [shuffledOptions, setShuffledOptions] = useState([]);

  useEffect(() => {
    if (selectedCategory && numQuestions && difficulty && questionType) {
      const fetchQuizData = async () => {
        try {
          const response = await axios.get(
            `https://opentdb.com/api.php?amount=${numQuestions}&category=${selectedCategory}&difficulty=${difficulty}&type=${questionType}`
          );
          setQuizData(response.data.results);
          shuffleOptions(response.data.results[0]);
        } catch (error) {
          console.error("Error fetching quiz data:", error);
        }
      };

      fetchQuizData();
    }
  }, [selectedCategory, numQuestions, difficulty, questionType]);

  useEffect(() => {
    if (timeLeft === 0) {
      handleAnswerOptionClick(null); 
    }

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  useEffect(() => {
    if (quizData.length > 0) {
      shuffleOptions(quizData[currentQuestion]);
    }
  }, [currentQuestion, quizData]);

  const shuffleOptions = (question) => {
    const options = [...question.incorrect_answers, question.correct_answer];
    for (let i = options.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [options[i], options[j]] = [options[j], options[i]];
    }
    setShuffledOptions(options);
  };

  const handleAnswerOptionClick = (answer) => {
    if (answer === quizData[currentQuestion]?.correct_answer) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
      setTimeLeft(30); 
    } else {
      setShowScore(true);
    }
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleNumQuestionsChange = (event) => {
    setNumQuestions(event.target.value);
  };

  const handleDifficultyChange = (event) => {
    setDifficulty(event.target.value);
  };

  const handleQuestionTypeChange = (event) => {
    setQuestionType(event.target.value);
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(12);
    doc.text("Quiz Results", 10, 10);
    doc.text(`You scored ${score} out of ${quizData.length}`, 10, 20);

    quizData.forEach((question, index) => {
      const yOffset = 30 + index * 40;
      doc.text(`Question ${index + 1}: ${question.question}`, 10, yOffset);
      doc.text(`Your Answer: ${question.yourAnswer || 'Not Answered'}`, 10, yOffset + 10);
      doc.text(`Correct Answer: ${question.correct_answer}`, 10, yOffset + 20);
    });

    doc.save("quiz_results.pdf");
  };

  const getResultEmoji = () => {
    if (score === quizData.length) return "ðŸŽ‰ Excellent!";
    if (score >= quizData.length / 2) return "ðŸ˜Š Good job!";
    return "ðŸ˜ž Better luck next time!";
  };

  const isQuizReady = () => selectedCategory && numQuestions && difficulty && questionType;

  return (
    <div className="app bg-animation">
      <div className="container">
        <div className="progress mt-4">
          <div
            className="progress-bar"
            role="progressbar"
            style={{
              width: `${((currentQuestion + 1) / quizData.length) * 100}%`,
              transition: "width 0.5s ease-in-out",
            }}
            aria-valuenow={(currentQuestion + 1)}
            aria-valuemin="0"
            aria-valuemax={quizData.length}
          >
            {currentQuestion + 1}/{quizData.length}
          </div>
        </div>

        {quizData.length === 0 ? (
          <div className="loading">Loading quiz...</div>
        ) : (
          <div className="quiz-section">
            {showScore ? (
              <div className="alert alert-success text-center score-section">
                <h4>{getResultEmoji()}</h4>
                <h5>You scored {score} out of {quizData.length}</h5>
                <p>Here's a fun fact: Did you know? The shortest war in history was between Britain and Zanzibar on August 27, 1896. Zanzibar surrendered after 38 minutes!</p>
                <button
                  className="btn btn-primary"
                  onClick={generatePDF}
                >
                  Download Results as PDF
                </button>
              </div>
            ) : (
              <>
                <div className="card mb-3">
                  <div className="card-header bg-primary text-white">
                    <div className="question-count">
                      <h5>Question {currentQuestion + 1}/{quizData.length}</h5>
                    </div>
                  </div>
                  <div className="card-body">
                    <h5 className="card-title animate-text">
                      {quizData[currentQuestion]?.question}
                    </h5>
                    <div className="timer text-danger animate-text">
                      Time left: {timeLeft} seconds
                    </div>
                    <div className="answer-section mt-4">
                      {shuffledOptions.map((option, index) => (
                        <button
                          key={index}
                          className="btn btn-outline-primary btn-block mb-2 animate-text"
                          onClick={() => handleAnswerOptionClick(option)}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>
        )}
        <div className="category-selection mt-4">
          <label>Select Quiz Category:</label>
          <select
            className="form-select mt-2"
            value={selectedCategory}
            onChange={handleCategoryChange}
          >
            <option value="">Select a category</option>
            <option value="9">General Knowledge</option>
            <option value="17">Science & Nature</option>
            <option value="23">History</option>
          </select>
        </div>
        <div className="num-questions-selection mt-4">
          <label>Select Number of Questions:</label>
          <select
            className="form-select mt-2"
            value={numQuestions}
            onChange={handleNumQuestionsChange}
          >
            <option value="">Select number of questions</option>
            <option value="5">5 Questions</option>
            <option value="10">10 Questions</option>
            <option value="15">15 Questions</option>
            <option value="20">20 Questions</option>
          </select>
        </div>
        <div className="difficulty-selection mt-4">
          <label>Select Difficulty Level:</label>
          <select
            className="form-select mt-2"
            value={difficulty}
            onChange={handleDifficultyChange}
          >
            <option value="">Select difficulty</option>
            <option value="easy">Easy</option>
            <option value="medium">Medium</option>
            <option value="hard">Hard</option>
          </select>
        </div>
        <div className="question-type-selection mt-4">
          <label>Select Question Type:</label>
          <select
            className="form-select mt-2"
            value={questionType}
            onChange={handleQuestionTypeChange}
          >
            <option value="multiple">Multiple Choice</option>
            <option value="multiple_select">Multiple Select</option>
          </select>
        </div>
        <div className="start-button mt-4">
          <button
            className="btn btn-success"
            onClick={() => {
              if (isQuizReady()) {
                setCurrentQuestion(0);
                setScore(0);
                setShowScore(false);
                setTimeLeft(30);
            } else {
                alert("Please select all required options.");
              }
            }}
            disabled={!isQuizReady()}
          >
            Start Quiz
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
