import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
import { Howl } from 'howler';
import tickSound from './tick-sound.mp3';
import {
  FacebookShareButton,
  LinkedinShareButton,
  FacebookIcon,
  LinkedinIcon
} from 'react-share';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [quizData, setQuizData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [numQuestions, setNumQuestions] = useState("");
  const [difficulty, setDifficulty] = useState("");
  const [questionType, setQuestionType] = useState("multiple");
  const [timeLeft, setTimeLeft] = useState(30);
  const [shuffledOptions, setShuffledOptions] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showHint, setShowHint] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (selectedCategory && numQuestions && difficulty && questionType) {
      const fetchQuizData = async () => {
        try {
          const response = await axios.get(
            `https://opentdb.com/api.php?amount=${numQuestions}&category=${selectedCategory}&difficulty=${difficulty}&type=${questionType}`
          );
          setQuizData(response.data.results);
          shuffleOptions(response.data.results[0]);
        } catch (error) {
          console.error("Error fetching quiz data:", error);
        }
      };

      fetchQuizData();
    }
  }, [selectedCategory, numQuestions, difficulty, questionType]);

  useEffect(() => {
    if (timeLeft === 0) {
      handleAnswerOptionClick(null);
    }

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  useEffect(() => {
    if (quizData.length > 0) {
      shuffleOptions(quizData[currentQuestion]);
    }
  }, [currentQuestion, quizData]);

  const shuffleOptions = (question) => {
    const options = [...question.incorrect_answers, question.correct_answer];
    for (let i = options.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [options[i], options[j]] = [options[j], options[i]];
    }
    setShuffledOptions(options);
  };

  const handleAnswerOptionClick = (answer) => {
    const isCorrect = answer === quizData[currentQuestion]?.correct_answer;
    setUserAnswers((prevAnswers) => [
      ...prevAnswers,
      {
        question: quizData[currentQuestion].question,
        userAnswer: answer,
        correctAnswer: quizData[currentQuestion].correct_answer,
      },
    ]);
  
    if (isCorrect) {
      setScore(score + 1);
    }
  
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
      setTimeLeft(30);
    } else {
      setShowScore(true);
    }
  
    // Apply animation classes
    const optionButtons = document.querySelectorAll('.option-btn');
    optionButtons.forEach((button) => {
      if (button.textContent === answer) {
        button.classList.add(isCorrect ? 'correct' : 'incorrect');
      }
    });
    setTimeout(() => {
      optionButtons.forEach((button) => button.classList.remove('correct', 'incorrect'));
    }, 1000);
  };
  
  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleNumQuestionsChange = (event) => {
    setNumQuestions(event.target.value);
  };

  const handleDifficultyChange = (event) => {
    setDifficulty(event.target.value);
  };

  const handleQuestionTypeChange = (event) => {
    setQuestionType(event.target.value);
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(12);
    doc.text("Quiz Results", 10, 10);
    doc.text(`You scored ${score} out of ${quizData.length}`, 10, 20);

    userAnswers.forEach((answer, index) => {
      const yOffset = 30 + index * 40;
      doc.text(`Question ${index + 1}: ${answer.question}`, 10, yOffset);
      doc.text(`Your Answer: ${answer.userAnswer || 'Not Answered'}`, 10, yOffset + 10);
      doc.text(`Correct Answer: ${answer.correctAnswer}`, 10, yOffset + 20);
    });

    doc.save("quiz_results.pdf");
  };

  const getResultEmoji = () => {
    if (score === quizData.length) return "ðŸŽ‰ Excellent!";
    if (score >= quizData.length / 2) return "ðŸ˜Š Good job!";
    return "ðŸ˜ž Better luck next time!";
  };

  const getEmojiForQuestion = () => {
    if (currentQuestion % 2 === 0) return "ðŸ¤”";
    return "ðŸ’¡";
  };

  const isQuizReady = () => selectedCategory && numQuestions && difficulty && questionType;

  const handleRetakeQuiz = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
    setTimeLeft(30);
    setUserAnswers([]);
    setShowHint(false);
  };

  const getHint = () => {
    if (quizData.length > 0) {
      const hint = `The correct answer is one of these: ${shuffledOptions.join(", ")}`;
      return hint;
    }
    return "No hint available";
  };

  const handleDarkModeToggle = () => {
    setDarkMode(!darkMode);
  };

  const getChartData = () => {
    const data = {
      labels: quizData.map((_, index) => `Question ${index + 1}`),
      datasets: [
        {
          label: "User Answers",
          data: userAnswers.map(answer => answer.userAnswer === answer.correctAnswer ? 1 : 0),
          backgroundColor: "rgba(75, 192, 192, 0.2)",
          borderColor: "rgba(75, 192, 192, 1)",
          borderWidth: 1,
        },
      ],
    };
    return data;
  };

  return (
    <div className={`app ${darkMode ? "dark-mode" : ""}`}>
      <div className="container">
        <div className="progress mt-4">
          <div
            className="progress-bar"
            role="progressbar"
            style={{
              width: `${((currentQuestion + 1) / quizData.length) * 100}%`,
              transition: "width 0.5s ease-in-out",
            }}
            aria-valuenow={(currentQuestion + 1)}
            aria-valuemin="0"
            aria-valuemax={quizData.length}
          >
            {currentQuestion + 1}/{quizData.length}
          </div>
        </div>

        {quizData.length === 0 ? (
          <div className="loading">Loading quiz...</div>
        ) : (
          <div className="quiz-section">
            {showScore ? (
              <div className="alert alert-success text-center score-section">
                <h4>{getResultEmoji()}</h4>
                <h5>You scored {score} out of {quizData.length}</h5>
                <p>Here's a fun fact: Did you know? The shortest war in history was between Britain and Zanzibar on August 27, 1896. Zanzibar surrendered after 38 minutes!</p>
                <button
                  className="btn btn-primary"
                  onClick={generatePDF}
                >
                  Download Results as PDF
                </button>
                <button
                  className="btn btn-secondary mt-2"
                  onClick={handleRetakeQuiz}
                >
                  Retake Quiz
                </button>
                <div className="chart mt-4">
                  <Bar data={getChartData()} />
                </div>
                <div className="social-media-sharing mt-4">
                  <FacebookShareButton
                    url={window.location.href}
                    quote={`I scored ${score} out of ${quizData.length} in this quiz!`}
                    className="me-2"
                  >
                    <FacebookIcon size={32} round />
                  </FacebookShareButton>
                  <LinkedinShareButton
                    url={window.location.href}
                    title={`I scored ${score} out of ${quizData.length} in this quiz!`}
                    className="me-2"
                  >
                    <LinkedinIcon size={32} round />
                  </LinkedinShareButton>
                </div>
              </div>
            ) : (
              <div className="quiz-container">
                <h2 className="question">{quizData[currentQuestion]?.question}</h2>
                <div className="options">
                  {shuffledOptions.map((option, index) => (
                    <button
                      key={index}
                      className="btn btn-light option-btn"
                      onClick={() => handleAnswerOptionClick(option)}
                    >
                      {option}
                    </button>
                  ))}
                </div>
                <div className="hint-section mt-3">
                  <button
                    className="btn btn-warning"
                    onClick={() => setShowHint(!showHint)}
                  >
                    {showHint ? "Hide Hint" : "Show Hint"}
                  </button>
                  {showHint && <p className="hint">{getHint()}</p>}
                </div>
                <div className="timer mt-3">
                  <h5>Time Left: {timeLeft}s</h5>
                </div>
              </div>
            )}
          </div>
        )}

        <div className="settings mt-4">
          <div className="form-group">
            <label htmlFor="category">Category</label>
            <select
              id="category"
              className="form-control"
              value={selectedCategory}
              onChange={handleCategoryChange}
            >
              <option value="">Select Category</option>
              <option value="9">General Knowledge</option>
              <option value="17">Science: Computers</option>
              <option value="18">Science: Gadgets</option>
              <option value="21">Sports</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="numQuestions">Number of Questions</label>
            <input
              type="number"
              id="numQuestions"
              className="form-control"
              value={numQuestions}
              onChange={handleNumQuestionsChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="difficulty">Difficulty</label>
            <select
              id="difficulty"
              className="form-control"
              value={difficulty}
              onChange={handleDifficultyChange}
            >
              <option value="">Select Difficulty</option>
              <option value="easy">Easy</option>
              <option value="medium">Medium</option>
              <option value="hard">Hard</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="questionType">Question Type</label>
            <select
              id="questionType"
              className="form-control"
              value={questionType}
              onChange={handleQuestionTypeChange}
            >
              <option value="multiple">Multiple Choice</option>
              <option value="boolean">True/False</option>
            </select>
          </div>
          <button
            className="btn btn-primary mt-3"
            onClick={() => {
              if (isQuizReady()) handleRetakeQuiz();
            }}
            disabled={!isQuizReady()}
          >
            Start Quiz
          </button>
        </div>

        <div className="dark-mode-toggle mt-4">
          <button
            className="btn btn-dark"
            onClick={handleDarkModeToggle}
          >
            Toggle Dark Mode
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
