import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

function App() {
  const [domain, setDomain] = useState('');
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [timeLeft, setTimeLeft] = useState(10);
  const [quizReview, setQuizReview] = useState([]);
  const [isQuizCreator, setIsQuizCreator] = useState(false);
  const [newQuiz, setNewQuiz] = useState([]);

  useEffect(() => {
    if (domain) {
      axios.get(`https://opentdb.com/api.php?amount=10&category=${domain}&type=multiple`)
        .then(response => setQuestions(response.data.results))
        .catch(error => console.error('Error fetching questions:', error));
    }
  }, [domain]);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else {
      handleAnswer("");
    }
  }, [timeLeft]);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  const handleDomainSelection = (event) => {
    setDomain(event.target.value);
  };

  const handleAnswer = (answer) => {
    setQuizReview([...quizReview, { question: questions[currentQuestion].question, yourAnswer: answer, correctAnswer: questions[currentQuestion].correct_answer }]);
    if (answer === questions[currentQuestion].correct_answer) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
      setTimeLeft(10);
    } else {
      setCurrentQuestion(-1);
    }
  };

  const handleQuizCreation = (event) => {
    const { name, value } = event.target;
    setNewQuiz([...newQuiz, { [name]: value }]);
  };

  const submitQuiz = () => {
    // Logic to submit the quiz to a database
    console.log("Quiz Submitted:", newQuiz);
    setIsQuizCreator(false);
  };

  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <h2>Login to Quiz Maker</h2>
        <button className="btn btn-primary" onClick={handleLogin}>Login</button>
      </div>
    );
  }

  if (isQuizCreator) {
    return (
      <div className="quiz-creator">
        <h2>Create a New Quiz</h2>
        <input type="text" name="question" placeholder="Enter question" onChange={handleQuizCreation} />
        <input type="text" name="correct_answer" placeholder="Enter correct answer" onChange={handleQuizCreation} />
        <input type="text" name="incorrect_answers" placeholder="Enter incorrect answers separated by commas" onChange={handleQuizCreation} />
        <button className="btn btn-success" onClick={submitQuiz}>Submit Quiz</button>
      </div>
    );
  }

  if (!domain) {
    return (
      <div className="domain-selection">
        <h3>Select a Quiz Domain:</h3>
        <select onChange={handleDomainSelection} className="form-control">
          <option value="">Choose a domain</option>
          <option value="18">Science: Computers</option>
          <option value="23">History</option>
          <option value="9">General Knowledge</option>
        </select>
        <button className="btn btn-secondary mt-3" onClick={() => setIsQuizCreator(true)}>Create a Quiz</button>
        <button className="btn btn-danger mt-3" onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  if (currentQuestion >= 0) {
    return (
      <div className="quiz-container">
        <h2>{questions[currentQuestion].question}</h2>
        <div className="answers">
          {questions[currentQuestion].incorrect_answers.concat(questions[currentQuestion].correct_answer)
            .sort(() => Math.random() - 0.5)
            .map((answer, index) => (
              <button
                key={index}
                className="btn btn-primary btn-block"
                onClick={() => handleAnswer(answer)}
              >
                {answer}
              </button>
            ))}
        </div>
        <div className="progress my-4">
          <div
            className="progress-bar progress-bar-striped bg-success"
            role="progressbar"
            style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
            aria-valuenow={currentQuestion + 1}
            aria-valuemin="0"
            aria-valuemax={questions.length}
          >
            {currentQuestion + 1}/{questions.length}
          </div>
        </div>
        <p>Time left: {timeLeft} seconds</p>
      </div>
    );
  } else {
    return (
      <div className="results">
        <h2>Quiz Completed!</h2>
        <p>Your Score: {score}/{questions.length}</p>
        <div className="emoji">
          {score === questions.length ? "ðŸŽ‰" : score > questions.length / 2 ? "ðŸ˜Š" : "ðŸ˜¢"}
        </div>
        <div className="quiz-review">
          <h3>Quiz Review:</h3>
          <ul>
            {quizReview.map((item, index) => (
              <li key={index}>
                <strong>Q:</strong> {item.question}<br />
                <strong>Your Answer:</strong> {item.yourAnswer || "Time Out"}<br />
                <strong>Correct Answer:</strong> {item.correctAnswer}
              </li>
            ))}
          </ul>
        </div>
        <button className="btn btn-secondary mt-3" onClick={() => setDomain('')}>Try Another Quiz</button>
        <button className="btn btn-danger mt-3" onClick={handleLogout}>Logout</button>
      </div>
    );
  }
}

export default App;
