import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [quizData, setQuizData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("9"); // Default to General Knowledge
  const [timeLeft, setTimeLeft] = useState(30);

  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await axios.get(
          `https://opentdb.com/api.php?amount=10&category=${selectedCategory}&type=multiple`
        );
        setQuizData(response.data.results);
      } catch (error) {
        console.error("Error fetching quiz data:", error);
      }
    };

    fetchQuizData();
  }, [selectedCategory]);

  useEffect(() => {
    if (timeLeft === 0) {
      handleAnswerOptionClick(null); // Auto-move to the next question if time runs out
    }

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
      setTimeLeft(30); // Reset the timer for the next question
    } else {
      setShowScore(true);
    }
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
    setTimeLeft(30);
  };

  return (
    <div className="app bg-animation">
      <div className="container">
        {quizData.length === 0 ? (
          <div className="loading">Loading quiz...</div>
        ) : (
          <div className="quiz-section">
            {showScore ? (
              <div className="alert alert-success text-center score-section">
                <h4>You scored {score} out of {quizData.length}</h4>
              </div>
            ) : (
              <>
                <div className="card mb-3">
                  <div className="card-header bg-primary text-white">
                    <div className="question-count">
                      <h5>Question {currentQuestion + 1}/{quizData.length}</h5>
                    </div>
                  </div>
                  <div className="card-body">
                    <h5 className="card-title">
                      {quizData[currentQuestion]?.question}
                    </h5>
                    <div className="timer text-danger">
                      Time left: {timeLeft} seconds
                    </div>
                    <div className="answer-section mt-4">
                      {quizData[currentQuestion]?.incorrect_answers.map(
                        (answerOption, index) => (
                          <button
                            key={index}
                            className="btn btn-outline-primary btn-block mb-2"
                            onClick={() => handleAnswerOptionClick(false)}
                          >
                            {answerOption}
                          </button>
                        )
                      )}
                      <button
                        className="btn btn-primary btn-block"
                        onClick={() =>
                          handleAnswerOptionClick(
                            quizData[currentQuestion]?.correct_answer ===
                              quizData[currentQuestion]?.correct_answer
                          )
                        }
                      >
                        {quizData[currentQuestion]?.correct_answer}
                      </button>
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>
        )}
        <div className="category-selection">
          <label>Select Quiz Category:</label>
          <select
            className="form-select mt-2"
            value={selectedCategory}
            onChange={handleCategoryChange}
          >
            <option value="9">General Knowledge</option>
            <option value="17">Science & Nature</option>
            <option value="23">History</option>
            {/* Add more categories as desired */}
          </select>
        </div>
      </div>
    </div>
  );
}

export default App;
